import React, { useState } from 'react';
import CardView from '../cardView/CardView';

import MenuIcon from '../../static/menu-icon.svg';
import './styles/card.scss';

import ImagePopup from '../imagePopup/imagePopup';

const Card = ({ images }) => {
  const [smallImg, bigImg] = images;
  const [openView, setOpenView] = useState(false);
  const [showImgPopup, setShowImgPopup] = useState(false);

  const handleImagePopup = () => {
    setShowImgPopup(!showImgPopup);
  };

  const handleDescriptionModal = () => {
    setOpenView(!openView);
  };

  return (
    <>
      <article className="card-skeleton">
        <div className="card-skeleton__container">
          <div
            className="card-skeleton__image-wrapper"
            onClick={handleImagePopup}
          >
            <div
              className="card-skeleton__background-image"
              style={{
                backgroundImage: `url(${smallImg})`,
                backgroundClip: 'content-box',
              }}
            ></div>
          </div>

          <div className="card-skeleton__tags">
            <span className="card-skeleton__tags card-skeleton__tags_isPrimary">
              ANIMALS
            </span>
            <span className="card-skeleton__tags card-skeleton__tags_isSecondary">
              WILDLIFE WATCH
            </span>
          </div>
          <div onClick={handleDescriptionModal}>
            <div className="card-skeleton__description">
              <span className="card-skeleton__description-text">
                Siberian tigers are being hunted at night for their body parts
              </span>
            </div>

            <div className="card-skeleton__footer">
              <img
                src={MenuIcon}
                alt="menu-icon"
                className="card-skeleton__menu-icon"
              />
              <span className="card-skeleton__menu-text">VIEW</span>
            </div>
          </div>
        </div>
      </article>

      {openView ? (
        <CardView
          src={bigImg}
          title="Wolf"
          handleClick={handleDescriptionModal}
          setOpenView={setOpenView}
        />
      ) : null}

      {showImgPopup ? (
        <ImagePopup {...images} handleImagePopup={handleImagePopup} />
      ) : null}
    </>
  );
};

export default Card;
