import React, { createRef, useEffect, useState } from 'react';

import CardSkeleton from '../cardSkeletons/CardSkeleton';

import CardView from '../cardView/CardView';
import ImagePopup from '../imagePopup/imagePopup';

import useDelayUnmount from '../../utils/useDelayUnmount';

import MenuIcon from '../../static/menu-icon.svg';
import './styles/card.scss';

const AsyncImage = ({ smallImg }) => {
  const [loadedSrc, setLoadedSrc] = React.useState(null);
  React.useEffect(() => {
    setLoadedSrc(null);
    if (smallImg) {
      const handleLoad = () => {
        setLoadedSrc(smallImg);
      };
      const image = new Image();
      image.addEventListener('load', handleLoad);
      image.src = smallImg;
      return () => {
        image.removeEventListener('load', handleLoad);
      };
    }
  }, [smallImg]);
  if (loadedSrc === smallImg) {
    return <img {...smallImg} alt="wolf" />;
  }
  return null;
};

const Card = ({ images, setShowCardSkeletons, showCardSkeletons }) => {
  const [bigImg, smallImg] = images;
  const [openView, setOpenView] = useState(false);
  const [showImgPopup, setShowImgPopup] = useState(false);
  const bgImage = createRef();

  const delayUnmount = useDelayUnmount(openView, 650);

  const setBackground = (url) => {
    // bgImage.current.style.backgroundImage = `url(${url})`;
    if (bgImage.current) {
      bgImage.current.src = url;
      setShowCardSkeletons();
    }
    console.log(bgImage.current);
  };

  const loadImage = (url) =>
    new Promise((resolve, reject) => {
      const img = new Image();
      img.addEventListener('load', () => resolve(img));
      img.addEventListener('error', (err) => reject(err));
      img.src = url;
    });

  useEffect(() => {
    loadImage(smallImg)
      .then((img) => {
        {
          if (bgImage) {
            bgImage.current.appendChild(img);
          }
        }
      })
      .catch((err) => console.error(err));
  }, [images]);

  const handleImagePopup = () => {
    setShowImgPopup(!showImgPopup);
  };

  const handleDescriptionModal = () => {
    setOpenView(!openView);
  };

  return (
    <>
      {<CardSkeleton />}
      <article
        className="card"
        style={showCardSkeletons ? { display: 'none' } : { display: 'flex' }}
      >
        <div className="card__container">
          <div
            className="card__image-wrapper"
            ref={bgImage}
            onClick={handleImagePopup}
          >
            {/* <img
              ref={bgImage}
              src=""
              alt=""
              className="card__background-image"
            /> */}
            {/* <i
             
              
              style={{
                // backgroundImage: `url(${smallImg})`,
                backgroundClip: 'content-box',
              }}
            ></i> */}
          </div>

          <div className="card__tags">
            <span className="card__tags card__tags_isPrimary">ANIMALS</span>
            <span className="card__tags card__tags_isSecondary">
              WILDLIFE WATCH
            </span>
          </div>
          <div onClick={handleDescriptionModal}>
            <div className="card__description">
              <span className="card__description-text">
                Siberian tigers are being hunted at night for their body parts
              </span>
            </div>

            <div className="card__footer">
              <img src={MenuIcon} alt="menu-icon" className="card__menu-icon" />
              <span className="card__menu-text">VIEW</span>
            </div>
          </div>
        </div>
      </article>

      {delayUnmount ? (
        <CardView
          src={bigImg}
          title="Wolf"
          handleClick={handleDescriptionModal}
          setOpenView={setOpenView}
          openView={openView}
        />
      ) : null}

      {showImgPopup ? (
        <ImagePopup {...images} handleImagePopup={handleImagePopup} />
      ) : null}
    </>
  );
};

export default Card;
