import Card from './partials/Card';
import './styles/hero.scss';
import withFetch from '../container/withFetch';

const WrappedComponent = (props) => {
  const {
    data: { wolves },
    isLoading,
    isError,
  } = props;

  const response = [];
  const url = '../../api/wolves.json';
  // 'https://gist.githubusercontent.com/bcnzer/2e1e392e355dc95b7f3da98a0b2ade9d/raw/328c14be889d1b833c1c60002cfa2a6babc9dbe4/mockwolfimages.json';
  const xhr = new XMLHttpRequest();
  xhr.open('GET', url);

  xhr.onreadystatechange = function () {
    if (xhr.readyState === 4) {
      response.push(JSON.parse(xhr.response));
    }
  };

  xhr.send();
  console.log('RESPONSE ', response);
  console.log('PROPS:     ', props);

  return (
    <main className="hero">
      <div className="hero__container">
        <div className="hero__head-container">
          <h1 className="hero__heading">LATEST STORIES</h1>
          <h3 className="hero__subheading">
            <span className="hero__subheading_isPrimary">Subscribe</span> for
            full access to read stories from National Geographic.
          </h3>
        </div>
        <div className="hero__cart-container">
          {wolves
            ? wolves.map((e, i) => <Card key={i} images={e.images} />)
            : null}
          {isError ? <h1>Oops! An error occured. ☹</h1> : null}
          {isLoading ? <h1>Loading... ⌚</h1> : null}
        </div>
      </div>
    </main>
  );
};

const Hero = withFetch(WrappedComponent, '../../../public/api/wolves.json');

export default Hero;
