import Menu from '../../menu/Menu';
import { createRef, useState } from 'react';
import useDelayUnmount from '../../../utils/useDelayUnmount';

const NavList = () => {
  const [showMenu, setShowMenu] = useState(false);
  const menu = createRef();
  const shouldRenderChild = useDelayUnmount(!showMenu, 500);

  const handleToggleClicked = () => {
    setShowMenu(!showMenu);
    if (showMenu) {
      console.log(shouldRenderChild);
    }
  };

  return (
    <>
      <ul className="navigation__list-container">
        <li
          className="navigation__menu"
          ref={menu}
          onClick={handleToggleClicked}
        >
          <span className="navigation__text">Menu</span>
        </li>
      </ul>
      {showMenu ? <Menu /> : null}
    </>
  );
};

export default NavList;
