import { useEffect } from 'react';
import Portal from '../../utils/CreatePortal.jsx';
import './styles/cardView.scss';

const CardView = ({ src, title }) => {
  useEffect(() => {
    const rect = document
      .getElementsByClassName('cardView__portal')[0]
      .getBoundingClientRect();

    rect.top = 0;
    rect.left = 0;
  }, []);
  return (
    <Portal className="cardView__portal">
      <figure>
        <img src={src} alt={title} />
      </figure>
    </Portal>
  );
};

export default CardView;
